import React, { useState } from 'react';
import { Element, scroller } from 'react-scroll';
import LayoutHeader from './components/LayoutHeader';
import LayoutFooter from './components/LayoutFooter';
import PageHome from './components/PageHome';
import PageManifesto from './components/PageManifesto';
import PageLegal from './components/PageLegal';
import PagePrivacy from './components/PagePrivacy';
import PageAbout from './components/PageAbout';
import PageServices from './components/PageServices';
import PageIdeas from './components/PageIdeas';
import PagePodcast from './components/PagePodcast';

export default function App() {
  const [language, setLanguage] = useState('es');

  // Scroll suave a la sección
  const scrollToSection = (section) => {
    scroller.scrollTo(section, {
      duration: 600,
      delay: 0,
      smooth: 'easeInOutQuart',
      offset: -70, // ajusta según el alto del header
    });
  };

  return (
    <div className="min-h-screen flex flex-col bg-gradient-to-b from-[#FFF9ED] via-[#B0ACA3] to-[#0A0A0A]">
      <LayoutHeader
        language={language}
        onLanguageChange={setLanguage}
        onSectionClick={scrollToSection}
      />
      <main className="flex-grow pt-20">
        <Element name="home">
          <PageHome language={language} onSectionClick={scrollToSection} />
        </Element>
        <Element name="manifesto">
          <PageManifesto language={language} />
        </Element>
        <Element name="ideas">
          <PageIdeas language={language} />
        </Element>
        <Element name="podcast">
          <PagePodcast language={language} />
        </Element>
        <Element name="services">
          <PageServices language={language} />
        </Element>
        <Element name="about">
          <PageAbout language={language} />
        </Element>
        {/* Legal y Privacy solo desde el footer */}
        <Element name="legal">
          <PageLegal language={language} />
        </Element>
        <Element name="privacy">
          <PagePrivacy language={language} />
        </Element>
      </main>
      <LayoutFooter language={language} onSectionClick={scrollToSection} />
    </div>
  );
}
