import React from 'react';

export default function LayoutHeader({ language, onLanguageChange, onSectionClick }) {
  return (
    <header className="fixed top-0 left-0 w-full bg-black text-white z-50 flex justify-between items-center px-6 py-4">
      <nav className="flex gap-6">
        <button onClick={() => onSectionClick('home')}>Home</button>
        <button onClick={() => onSectionClick('manifesto')}>Manifesto</button>
        {/* Agrega m√°s botones para otras secciones */}
      </nav>
      <div>
        <button onClick={() => onLanguageChange('es')}>ES</button>
        <button onClick={() => onLanguageChange('en')}>EN</button>
      </div>
    </header>
  );
}
