import React, { useState, useEffect, useRef } from 'react';

export default function PageContact({ language }) {
  const t = {
    es: { titulo: "Contacto", texto: "Envíanos tu mensaje..." },
    en: { titulo: "Contact", texto: "Send us your message..." }
  }[language];

  const [formData, setFormData] = useState({ name: '', email: '', message: '' });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
    alert(language === 'es' ? 'Mensaje enviado con éxito!' : 'Message sent successfully!');
    setFormData({ name: '', email: '', message: '' });
  };

  const sectionRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            entry.target.classList.add('animate-fade-in-up');
          } else {
            entry.target.classList.remove('animate-fade-in-up');
          }
        });
      },
      { threshold: 0.1 }
    );

    if (sectionRef.current) {
      observer.observe(sectionRef.current);
    }

    return () => {
      if (sectionRef.current) {
        observer.unobserve(sectionRef.current);
      }
    };
  }, []);

  return (
    <section className="max-w-3xl mx-auto px-4 py-16 pt-24">
      <h2 className="text-4xl font-bold text-gold mb-8 text-center">
        {t.titulo}
      </h2>
      <div className="space-y-6 text-[#0A0A0A] text-lg leading-relaxed">
        <p><strong>{t.controlador}</strong><br />{t.nombreControlador}</p>
        <p className="text-sm mt-4">{t.ultimaActualizacion}</p>
      </div>
    </section>
  );
};
