import React from 'react';
import logo from '../assets/logo.svg';

const homeContent = {
  es: {
    bienvenida: "Bienvenido a",
    titulo: "TheShakhestudium",
    parrafo: "En silencio y contracorriente. No para vencer, sino para comprender. Este espacio existe para cuestionar la narrativa y dar voz a lo que nadie se atreve a decir en público.",
    cta: (
      <>
        Atrévete a cuestionar.<br />
        Rompe el silencio.<br />
        <span className="cta-highlight">Comienza</span> el viaje.
      </>
    ),
  },
  en: {
    bienvenida: "Welcome to",
    titulo: "TheShakhestudium",
    parrafo: "Silent and upstream. Not to prevail, but to perceive. This space exists to question the narrative and give voice to what no one dares to say out loud.",
    cta: (
      <>
        Dare to question.<br />
        Break the silence.<br />
        <span className="cta-highlight">Begin</span> the journey.
      </>
    ),
  }
};

export default function PageHome({ language, onSectionClick }) {
  const t = homeContent[language];

  return (
    <section className="relative flex items-center justify-center min-h-[80vh] w-full">
      {/* Logo al fondo */}
     <img
  src={logo}
  alt="TheShakhestudium Logo"
  className="absolute inset-0 mx-auto my-auto w-[55vw] max-w-4xl opacity-10 z-0 pointer-events-none select-none"
  style={{ top: '50%', left: '50%', transform: 'translate(-50%, -50%)', minWidth: 320 }}
/>
      <div className="relative z-10 flex flex-col items-center w-full">
        <span className="text-2xl mb-2">{t.bienvenida}</span>
        <h1
          className="font-bold text-5xl md:text-6xl mb-6 transition cursor-pointer title-dorado"
          tabIndex={0}
          onMouseOver={e => e.currentTarget.classList.add('animate-cta')}
          onMouseOut={e => e.currentTarget.classList.remove('animate-cta')}
          onFocus={e => e.currentTarget.classList.add('animate-cta')}
          onBlur={e => e.currentTarget.classList.remove('animate-cta')}
        >
          {t.titulo}
        </h1>
        <p className="text-lg max-w-2xl mb-8 text-center">{t.parrafo}</p>
        <button
          className="cta-minimal font-bold text-xl mt-2 focus:outline-none transition"
          style={{ background: 'none', border: 'none', color: 'inherit', padding: 0 }}
          onClick={() => onSectionClick && onSectionClick('manifesto')}
        >
          {t.cta}
        </button>
      </div>
      {/* Estilos animados */}
      <style>{`
        .title-dorado {
          color: #BD9435;
          text-shadow: 0 0 14px #fff4d1, 0 0 24px #bd9435;
          transition: text-shadow 0.4s, letter-spacing 0.4s;
        }
        .title-dorado.animate-cta {
          text-shadow: 0 0 40px #fff8e0, 0 0 64px #bd9435;
          letter-spacing: 2.5px;
          animation: cta-pulse 1.2s infinite alternate;
        }
        .cta-highlight {
          color: #BD9435;
          font-weight: bold;
          transition: text-shadow 0.3s;
        }
        .cta-minimal:hover .cta-highlight,
        .cta-minimal:focus .cta-highlight {
          text-shadow: 0 0 8px #f7e6b5, 0 0 24px #bd9435;
          animation: cta-pulse 1s infinite alternate;
        }
        @keyframes cta-pulse {
          from { text-shadow: 0 0 8px #f7e6b5, 0 0 24px #bd9435; }
          to   { text-shadow: 0 0 24px #f7e6b5, 0 0 40px #bd9435; letter-spacing: 1.5px; }
        }
      `}</style>
    </section>
  );
}
