export default function LayoutFooter({ language, onSectionClick }) {
  const sectionRef = useRef(null);

  useEffect(() => {
    const node = sectionRef.current;
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          // ...tu l칩gica...
