      },
      { threshold: 0.1 }
    );
    if (node) observer.observe(node);
    return () => {
      if (node) observer.unobserve(node);
    };
  }, []);

  return (
    <footer ref={sectionRef} className="bg-black text-white py-8 px-6 flex flex-col md:flex-row justify-between items-center">
