import React from 'react';

const labels = {
  home: { es: 'Inicio', en: 'Home' },
  manifesto: { es: 'Manifiesto', en: 'Manifesto' },
  podcast: { es: 'Podcast', en: 'Podcast' },
  ideas: { es: 'El Pensadero', en: 'The Thinkery' },
  contact: { es: 'Contacto', en: 'Contact' }
};

export default function LayoutHeader({ language, onLanguageChange, onSectionClick }) {
  return (
    <header className="bg-[#0A0A0A] text-white px-6 py-4 flex flex-wrap justify-between items-center shadow-md fixed top-0 w-full z-50">
      <div className="font-bold text-xl cta-highlight text-center" style={{ letterSpacing: '1px' }}>
  TheShakhestudium
</div>
      <nav className="flex flex-wrap gap-4">
        {Object.keys(labels).map((section) => (
          <button
            key={section}
            onClick={() => onSectionClick(section)}
            className="hover:underline"
          >
            {labels[section][language]}
          </button>
        ))}
      </nav>
      <div className="ml-4">
        <button onClick={() => onLanguageChange('es')} className={language === 'es' ? 'font-bold underline' : ''}>ES</button>
        <span className="mx-1">|</span>
        <button onClick={() => onLanguageChange('en')} className={language === 'en' ? 'font-bold underline' : ''}>EN</button>
      </div>
    </header>
  );
}
